import React from 'react';

function SubjectSelector() {
  const subjects = ['Mathematics', 'Physics', 'Chemistry', 'Biology'];

  return (
    <div>
      <h2>Select a Subject</h2>
      <ul>
        {subjects.map(subject => <li key={subject}>{subject}</li>)}
      </ul>
    </div>
  );
}

export default SubjectSelector;

